$(function(){$(".newCaptcha").click(function(n){var r=$(this),i=r.prev(".captchaContainer").find("img").first().attr("src"),t;i=i.replace(/(.*?)\?r=\d+\.\d+$/,"$1");t=new Image;$(t).on("load",function(){r.prev(".captchaContainer").empty().append(t)});t.src=i+"?r="+Math.random();n.preventDefault()})});